package Nums;

import java.util.Scanner;

public class Addnos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a,b,add;
		System.out.println("Enter two numbers");
		Scanner scan = new Scanner(System.in);
		a=scan.nextInt();
		b=scan.nextInt();
		add=a+b;
		System.out.println("The sum is "+add);

	}

}
